import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Activity } from 'src/app/activity/Models/Activity';
import { ActivityService } from 'src/app/activity/Services/activity.service';
import { Project } from 'src/app/projects/Models/project';

@Component({
  selector: 'app-add-activity',
  templateUrl: './add-activity.component.html',
  styleUrls: ['./add-activity.component.css']
})
export class AddActivityComponent implements OnInit {
constructor(private service:ActivityService) {}
projectId:any;
employees:any[]=[];
projects:Project[]=[];
  ngOnInit(): void {
    this.service.getAllProject().subscribe((res)=>{
    this.projects=res;
    console.log(res);
    })
  }

activity:Activity={
  id: 0,
  title: '',
  description: '',
  activityType: '',
  projectId: 0,
  assignedBy: 0,
  assignedTo: 0,
  assignDate: '',
  startDate: '',
  dueDate: '',
  status: ''
};
activityform=new FormGroup({
  title:new FormControl(),
  activitytype:new FormControl(),
  description:new FormControl(),
  assigndate:new FormControl(),
  startdate:new FormControl(),
  duedate:new FormControl(),
  projectid:new FormControl(),
  assignedby:new FormControl(),
  assignedto:new FormControl(),
  createddate:new FormControl(),
  status:new FormControl(),


});




onSubmit(){
 this.service.addActivity(this.activity);
 console.log(this.activityform.value)
}


onChange(e:any){
 this.projectId= e.target.value;
  this.service.getAllEmployees(this.projectId).subscribe((res)=>{
    console.log(res);
    this.employees=res;
  })
}

}
<form [formGroup]="activityform" (ngSubmit)="onSubmit()">

<div> <label >Project :
  <select id="projectid" name="projectid"  formControlName="projectid"(change)="onChange($event)">
      <option *ngFor="let project of projects" [value]="project.id"  >{{project.title}}</option>
    </select>
  </label>


  <label for="assignedto"  >Assigned To :
    <select id="assignedto" name="assignedto"  formControlName="assignedto">
      <option *ngFor="let employee of employees" [value]="employee.employeeId"  >{{employee.fullName}}</option>
    </select>
  </label>


</div>


    <p>
      <label for="title">Title :
      <input type="text" id="title" name="title" formControlName="title">
    </label>

      <label for="activitytype">Activity Type :
      <select id="activitytype" name="activitytype" formControlName="activitytype">
        <option value="task">task</option>
        <option value="userstory">userstory</option>
      </select>
    </label>

      <label for="description">Description :
      <input type="text" id="description" name="description" formControlName="description">
    </label>
    </p>


     <p>
      <label for="assigndate">Assign Date :
      <input type="date" id="assigndate" name="assigndate" formControlName="assigndate">
    </label>
     </p>
      <!-- <p>
        <label for="startdate">Start Date :</label>
        <input type="text" id="startdate" name="startdate" formControlName="startdate">
      </p> -->

      <p>

       </p>

       <p>

      </p> 
      <!-- <p>
        <label for="assignedby">Assignedby </label>
        <input type="text" id="assignedby" name="assignedby" formControlName="assignedby">
      </p>  -->
      <p>

      </p>
      <!-- <p>
        <label for="createddate">Created Date </label>
        <input type="text" id="createddate" name="createddate" formControlName="createddate">
      </p> -->

    <!-- <p>
      <label for="status" >Status :</label>
      <input type="text" id="lname" name="lname"   disabled  >
    </p> -->

    <p>
      <button type="submit" >Submit</button>
    </p>

</form>
