import { Component, OnInit } from '@angular/core';
import { ActivityService } from '../../Services/activity.service';
import { Project } from 'src/app/projects/Models/project';
import { Activity } from '../../Models/Activity';

@Component({
  selector: 'app-project-activities',
  templateUrl: './project-activities.component.html',
  styleUrls: ['./project-activities.component.css']
})
export class ProjectActivitiesComponent implements OnInit{
  projects:Project[]=[];
  activities:Activity[]=[];
  projectId:number=0;
  constructor(private service:ActivityService){}

  ngOnInit(): void {
    this.service.getAllProjects().subscribe((res)=>{
this.projects=res;
this.projectId=this.projects[0].id;
console.log(res);
    })
  }

  getActivitiesOfSelectedProject(projectId:any){
   this.projectId=projectId.target.value;
   console.log(this.projectId);
   this.service.getAllActivitiesByProject(this.projectId).subscribe((res)=>{
   this.activities=res;
   console.log(res);
   })
   }

}
