&nbsp;&nbsp;&nbsp;&nbsp;

<div>
  <label for="project">Project Name:</label>
  <select id="project" name="project" (change)="getSelectedProjectId($event)">
    <option *ngFor="let project of projects" [value]="project.id">{{project.title}}</option>
  </select>
</div>&nbsp;&nbsp;&nbsp;&nbsp;




<div class="checkbox-container">
  <label>
    <input type="checkbox" [(ngModel)]="showTodoTasks"> ToDo
  </label>
  <label>
    <input type="checkbox" [(ngModel)]="showInProgressTasks"> Inprogress
  </label>
  <label>
    <input type="checkbox" [(ngModel)]="showCompletedTasks"> Completed
  </label>
</div>

<div class="task-list">
  <div >
    <div id="tasksContainer">
      <div class="container">
        <div class="card">
          <table class="table-bordered text-center">
            <thead>
              <tr>
                <th>activityType</th>
                <th>title</th>
                <th>description</th>
                <th>assignDate</th>
                <th>assignedBy</th>
                <th>assignedTo</th>
                <th>startDate</th>
                <th>dueDate</th>
                <th>status</th>
              </tr>
            </thead>
            <tbody *ngFor="let activity of todoTasks">
              <tr>
                <td>{{activity.activityType}}</td>
                <td>{{activity.title}}</td>
                <td>{{activity.description}}</td>
                <td>{{activity.assignDate}}</td>
                <td>{{activity.assignedBy}}</td>
                <td>{{activity.assignedTo}}</td>
                <td>{{activity.startDate}}</td>
                <td>{{activity.dueDate}}</td>
                <td>{{activity.status}}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div> 

  </div>

  <div >
    <div id="tasksContainer" >
      <div class="container">
        <h2> Activities Of Employees</h2>
        <div class="card">
          <table class="table-bordered text-center">
            <thead>
              <tr>
                <th>Select</th>
                <th>activityType</th>
                <th>title</th>
                <th>description</th>
                <th>assignDate</th>
                <th>assignedBy</th>
                <th>assignedTo</th>
                <th>startDate</th>
                <th>dueDate</th>
                <th>status</th>
              </tr>
            </thead>
            <tbody *ngFor="let activity of inprogressTasks">
              <tr>
                <td>{{activity.activityType}}</td>
                <td>{{activity.title}}</td>
                <td>{{activity.description}}</td>
                <td>{{activity.assignDate}}</td>
                <td>{{activity.assignedBy}}</td>
                <td>{{activity.assignedTo}}</td>
                <td>{{activity.startDate}}</td>
                <td>{{activity.dueDate}}</td>
                <td>{{activity.status}}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div> 
  </div>

  <div >

    <div id="tasksContainer" *ngIf="showCompletedTasks">
      <div class="container">
        <h2> Activities Of Employees</h2>
        <div class="card">
          <table class="table-bordered text-center">
            <thead>
              <tr>
                <th>Select</th>
                <th>activityType</th>
                <th>title</th>
                <th>description</th>
                <th>assignDate</th>
                <th>assignedBy</th>
                <th>assignedTo</th>
                <th>startDate</th>
                <th>dueDate</th>
                <th>status</th>
              </tr>
            </thead>
            <tbody *ngFor="let activity of completedTasks">
              <tr>
                <td>{{activity.activityType}}</td>
                <td>{{activity.title}}</td>
                <td>{{activity.description}}</td>
                <td>{{activity.assignDate}}</td>
                <td>{{activity.assignedBy}}</td>
                <td>{{activity.assignedTo}}</td>
                <td>{{activity.startDate}}</td>
                <td>{{activity.dueDate}}</td>
                <td>{{activity.status}}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div> 
  </div>
</div>
import { Component, OnInit } from '@angular/core';
import { ActivityService } from '../../Services/activity.service';
import { Project } from 'src/app/projects/Models/project';
import { Activity } from '../../Models/Activity';

@Component({
  selector: 'app-project-activities',
  templateUrl: './project-activities.component.html',
  styleUrls: ['./project-activities.component.css'],
})
export class ProjectActivitiesComponent implements OnInit {
  projects: Project[] = [];
  activities: Activity[] = [];
  projectId: number = 0;
  isFalse:boolean=false;
  todoTasks: Activity[] = []; // Replace 'any' with the actual type of your tasks
  inprogressTasks: Activity[] = [];
  completedTasks: Activity[] = [];
  showTodoTasks: boolean = false;
  showInProgressTasks: boolean = false;
  showCompletedTasks: boolean = false;

  constructor(private service: ActivityService) {}

  ngOnInit(): void {
