import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { ActivityService } from '../../Services/activity.service';
import { Activity } from '../../Models/Activity';

@Component({
  selector: 'app-activity-list',
  templateUrl: './activity-list.component.html',
  styleUrls: ['./activity-list.component.css']
})
export class ActivityListComponent implements OnInit{
  @Output() selectedActivities=new EventEmitter<Activity>()
  constructor(private service :ActivityService){}
  activities:Activity[]=[];
  projectId:number=2;
  assignedto:number=10;
  ngOnInit(): void {
   this.service.getAllActivities(this.projectId,this.assignedto).subscribe((res)=>{
   this.activities=res;
   this.selectedActivities.emit(this.activities[0]);
   })
  }



  sendEvent(activity:Activity){
    this.selectedActivities.emit(activity);
  }

}
<div class="container">
    <h2> My Activities</h2>
    <div class="card">
        <table class="table-bordered text-center">
            <thead>
                <tr>
                    <th>AssignedDate</th>
                    <th>Title</th>
                    <th>Status</th>
                   </tr>
            </thead>
            <tbody *ngFor="let activity of activities">
                <tr>
                    <td> {{activity.assignDate |date}}</td>
                    <td><a href="#"(click)="sendEvent(activity)">{{activity.title}}</a></td>
                    <td> {{activity.status}}</td>
                </tr>
            </tbody>
        </table>
    </div>
