 <div class="container">
    <h2> UserStories List</h2>
    <div class="card">
        <table class="table-bordered text-center">
            <thead>
                <tr>
                    <th>title</th>
                    <th>description</th>
                </tr>
            </thead>
            <tbody *ngFor="let userstory of userstories">
                <tr>
                    <a href="#" (click)="onClickUserStory(userstory)" ><td >{{userstory.title}}</td></a>
                    <td> {{userstory.description}}</td>
                    
                </tr>
            </tbody>
        </table>
    </div>
