import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Activity } from 'src/app/activity/Models/Activity';
import { ActivityService } from 'src/app/activity/Services/activity.service';

@Component({
  selector: 'app-update-activity',
  templateUrl: './update-activity.component.html',
  styleUrls: ['./update-activity.component.css']
})
export class UpdateActivityComponent implements OnInit{
  constructor(private service:ActivityService){}
  projectId:number=4
  employeeId:number=15
  activityId:number=0;
  isFalse: boolean = false;
  activity:Activity={
    id: 0,
    title: '',
    description: '',
    activityType: '',
    projectId: 0,
    projectName: 0,
    assignedBy: 0,
    assignedTo: 0,
    assignDate: '',
    startDate: '',
    dueDate: '',
    status: ''
  };

 status:string="" ;

  activities:Activity[]=[];
  ngOnInit(): void {
   this.service.getAllNotStartedActivities(this.projectId,this.employeeId).subscribe((res)=>{
    this.activities=res;
    console.log(res);
   })
  }


  onChange(activity:Activity){


   this.activityId=activity.id;

   this.isFalse=true;

  console.log(activity.id);
   }

update(){

  this.status=this.status;
   console.log("hii"+this.activity);
  this.service.updateActivity(this.status,this.activityId,this.projectId,this.employeeId).subscribe((res)=>{
    console.log("hello"+res)
   })
}

}
